import React, {useEffect, useState} from "react";
import {
  Divider,
  Tooltip,
  List,
  Avatar,
  Spin,
  Tabs,
  Input,
  Button,
} from 'antd' ;
import { LogoutOutlined } from "@ant-design/icons";
import { useNavigate } from "react-router-dom";



const tokens = [
  {
    symbol: "ETH",
    name: "Ethereum",
    balance: 100000000000,
    decimals: 18,
  },
  {
    symbol: "LINK",
    name: "Chainlink",
    balance: 100000000000,
    decimals: 18,
  },
  {
    symbol: "UNI",
    name: "Uniswap",
    balance: 100000000000,
    decimals: 18,
  },
  {
    symbol: "MATIC",
    name: "Polygon",
    balance: 100000000000,
    decimals: 18,
  },
];

const nfts = [
  "https://nft-preview-media.s3.us-east-1.amazonaws.com/evm/0x1/0xd774557b647330c91bf44cfeab205095f7e6c367/0xfb76f9ef3adabc27d77c615959f9e22dea24ac7d6a10af3458b3481e5f5e0f10/high.png",
  ,
  "https://nft-preview-media.s3.us-east-1.amazonaws.com/evm/0x1/0x749f5ddf5ab4c1f26f74560a78300563c34b417d/0x90cae88ffc909feab8e4df76abd0652dee98b7bffab29597d898260d91c20aa1/high.jpeg",
];

function WalletView({wallet , setWallet, seedPhrase, setSeedPhrase , selectedChain,}) {
  const navigate = useNavigate() ;
  function logout() {
    setSeedPhrase(null);
    setWallet(null);
    navigate("/")
  }







  return (
    <>
      <div className="content">
        <div className="logoutButton" onClick={logout}>
          <LogoutOutlined />

        </div>
        <div className="walletName">
          Wallet
        </div>

        <Tooltip title = {wallet}>
          <div>
            {wallet.slice(0, 4)}...{wallet.slice(38)}
          </div>
        </Tooltip>

      </div>
    </>
  );
}

export default WalletView;
